#include "Random2.h"#include "Normals.h"unsigned long RandomBase::GetDimensionality() const{	return Dimensionality;}void RandomBase::GetGaussians(MJArray& variates){	GetUniforms(variates);	for (unsigned long i = 0; i < Dimensionality; i++)	{		double x = variates[i];		variates.setValue(i,InverseCumulativeNormal(x));	}}void RandomBase::ResetDimensionality(unsigned long NewDimensionality) {	Dimensionality = NewDimensionality;}RandomBase::RandomBase(): Dimensionality(3){}